def update_instruments(context):
    match context:
        case 'md':
            return update_md_instruments()
        case 'portafm':
            return update_portafm_instruments()
        case 'dexed':
            return update_dexed_instruments()
        case 'opl':
            return update_opl_instruments()
        case 'tyrelln6':
            return update_tyrelln6_instruments()
        case _:
            print(f'Unknown vst {context}')
            assert False


