def setup_instruments(client: SC3Client, instrument_count,
                      context, vst_name, preset_name: Optional[str],
                      pitch_bend_sensitivity):
    data = [instrument_count] + [vst_name] * instrument_count
    client.send_msg('/vst_setup', data, sleep=5.0)

    for i in range(instrument_count):
        if preset_name is not None:
            client.load_preset(i, preset_name)
        Synth(i, pitch_bend_sensitivity)

    return update_instruments(context)


def assign_instrument(synth_idx, synth_name, instruments, client,
                      synth_name2=None, proportion=None,
                      shift=0, param_count=None):
    if synth_name2 is not None:
        mixed_instrument = [(instruments[synth_name][idx] * proportion +
                            instruments[synth_name2][idx] * (1 - proportion))
                            for idx in range(len(instruments[synth_name]))]
    else:
        mixed_instrument = instruments[synth_name]
    if param_count is None:
        param_count = len(mixed_instrument)
    data = [synth_idx, shift] + mixed_instrument[shift:shift+param_count]
    timetag = time.time()
    client.send_msg('/set_instrument', data, timetag=timetag)
    return
